ValidationRule.errorConditionFormula:
OR(
/*Si completas tarjeta de credito*/
AND(
RecordType.DeveloperName = "Interfaz_financiera",
OR(
NOT(ISBLANK(Cdigo_debito_por_ACA__c)),
NOT(ISBLANK(TEXT(Tipo_de_debito_por_ACA__c))),
NOT(ISBLANK( CBU__c ))
),
OR(
NOT(ISBLANK(TEXT(Tarjeta_de_credito__c))),
NOT(ISBLANK(Numero_de_tarjeta_de_credito__c))
)
),
/*Si completas c√≥digo debito*/
AND(
RecordType.DeveloperName = "Interfaz_financiera",
OR(
NOT(ISBLANK(TEXT(Tarjeta_de_credito__c))),
NOT(ISBLANK(Numero_de_tarjeta_de_credito__c)),
NOT(ISBLANK( CBU__c ))
),
OR(
NOT(ISBLANK(Cdigo_debito_por_ACA__c)),
NOT(ISBLANK(TEXT(Tipo_de_debito_por_ACA__c)))
)
),
/*Si completas CBU*/
AND(
RecordType.DeveloperName = "Interfaz_financiera",
NOT(ISBLANK( CBU__c )),
OR(
NOT(ISBLANK(TEXT(Tarjeta_de_credito__c))),
NOT(ISBLANK(Numero_de_tarjeta_de_credito__c)),
NOT(ISBLANK(Cdigo_debito_por_ACA__c)),
NOT(ISBLANK(TEXT(Tipo_de_debito_por_ACA__c)))
)
)
)
