ValidationRule.errorConditionFormula:
OR(
	AND(
		NOT(ISPICKVAL(Status, "CA-----N")),
		OR(
			AND(
				RecordType.DeveloperName = "Solicitud_de_despegues",
				OR(
					AND(
						ISPICKVAL(Account.Type, "FAM"),
						ISPICKVAL(Type,"TF")
					),
					AND(
						ISPICKVAL(Account.Type,"TIT"),
						NOT(ISPICKVAL(Type,"TF"))
					)
				)
			),
			AND(
				RecordType.DeveloperName = "Solicitud_de_alta_familiar",
				ISPICKVAL(Account.Type, "FAM")
			)
		)
	),
	AND(
		RecordType.DeveloperName = "Solicitud_de_baja",
		ISPICKVAL(Account.Type, "FAM")
	),
	AND(
		RecordType.DeveloperName = "Solicitud_de_cambio_de_plan",
		ISPICKVAL(Account.Type, "FAM")
	),
	AND(
		RecordType.DeveloperName = "Solicitud_de_segmento",
		ISPICKVAL(Account.Type, "FAM")
	),
	AND(
		RecordType.DeveloperName = "Interfaz_financiera",
		ISPICKVAL(Account.Type, "FAM")
	)
)
