ValidationRule.errorConditionFormula:
OR(
				AND(
         RecordType.Name = "Prestacional",
								OR(
				       ISPICKVAL(Caso__r.Type,"Odontologico"),
								   ISPICKVAL(Caso__r.Type,"Medicamento")
												)
								),
   	AND(
         RecordType.Name = "Odontologica",
								OR(
				       ISPICKVAL(Caso__r.Type,"Presacion"),
								   ISPICKVAL(Caso__r.Type,"Medicamento")
												)
								),
					AND(
         RecordType.Name = "Medicamento",
								OR(
				       ISPICKVAL(Caso__r.Type,"Odontologico"),
								   ISPICKVAL(Caso__r.Type,"PRestacion")
												)
								)
				)
